package com.qubuyer.bean.payment;import android.text.TextUtils;import java.util.HashMap;import java.util.Map;public class AliPayResultEntity {	private String resultStatus;	private String result;	private String memo;	public static final Map<String, String> sError;	static {		sError = new HashMap<>();		sError.put("9000", "支付成功");		sError.put("8000", "支付结果确认中");		sError.put("4000", "订单支付失败,如您没有安装支付宝钱包,请尝试安装支付宝钱包重新支付");		sError.put("4001", "数据格式不正确");		sError.put("4003", "该用户绑定的支付宝账户被冻结或不允许支付");		sError.put("4004", "该用户已解除绑定");		sError.put("4005", "绑定失败或没有绑定");		sError.put("4006", "订单支付失败");		sError.put("4010", "重新绑定账户");		sError.put("6000", "支付服务正在进行升级操作");		sError.put("6001", "取消支付操作");		sError.put("6002", "网络连接出错");		sError.put("7001", "网页支付失败");	}	public AliPayResultEntity(Map<String, String> rawResult) {		if (rawResult == null) {			return;		}		for (String key : rawResult.keySet()) {			if (TextUtils.equals(key, "resultStatus")) {				resultStatus = rawResult.get(key);			} else if (TextUtils.equals(key, "result")) {				result = rawResult.get(key);			} else if (TextUtils.equals(key, "memo")) {				memo = rawResult.get(key);			}		}	}	public String getResultStatus() {		return resultStatus;	}	public String getMemo() {		return memo;	}	public String getResult() {		return result;	}}